int firstDigit(int n) {
     int d = n;
     while(d >= 10) {
         d = d / 10;
     }
     return d;
}

boolean isMultiple(int a, int b) {
     int n = a;
     while(n >= b) {
         n = n - b;
     }
     return n == 0;
}

int intDivision(int a, int b) {
     int n = a;
     int d = 0;
     while(n >= b) {
         n = n - b;
         d = d + 1;
     }
     return d;
}

int powerOfTwo(int e) {
     int p = 1;
     int n = e;
     while(n > 0) {
         p = p * 2;
         n = n - 1;
     }
     return p;
}

int sumNaturals(int max) {
     int s = 0;
     int n = 1;
     while(n <= max) {
         s = s + n;
         n = n + 1;
     }
     return s;
}

int sumEvenBetween(int min, int max) {
     int s = 0;
     int n = min;
     if(n % 2 != 0) {
         n = n + 1;
     }
     while(n <= max) {
         s = s + n;
         n = n + 2;
     }
     return s;
}

char nextLetter(char l) {
     if(l == 'z') {
         return 'a';
     }
     else {
         return (char) (l + 1);
     }
}

int gcd(int a, int b) {
     while(b != 0) {
         int r = a % b;
         a = b;
         b = r;
     }
     return a;
}

int fib(int n) {
     if(n <= 1) {
         return n;
     }
     int a = 0;
     int b = 1;
     int i = 2;
     while(i != n) {
         int t = a + b;
         a = b;
         b = t;
         i = i + 1;
     }
     return a + b;
}
